import dictionary from "../dictionary.json";

interface Props{
    query: string;
}

export default function TermsList({query}:Props){
    const findMatches = (query:string) => {
        const regex = new RegExp(query, 'gi');
        const termsArray:string[] = [];
        [dictionary].forEach(obj => {
            for (let [term, def] of Object.entries(obj)) {
                termsArray.push(term);
            }
        })
        return termsArray.filter((val:string) => val.match(regex))
    }

    const getTermsHtmlArray = (query:string) => {
        const matchArray = findMatches(query);
        return matchArray.map((term, index) => {
            const regex = new RegExp(query, 'gi');
            const itemValue =  term.replace(regex, query);
            return (
                <li id="${index}"
                    className="text-sm break-words text-gray-200 cursor-pointer hover:text-gray-900 hover:bg-lexicblue-300 p-1"
                    data-key="${term}">
                    <span>{itemValue}</span>
                </li>
            )
        })
    }

    return (
        <div>
            {getTermsHtmlArray(query).join('')}
        </div>
    )

}
