import dictionary from "../dictionary.json";

interface Props{
    query: string;
    count: number;
}

export default function TermsList({query, count}:Props){
    const findMatches = (query:string) => {
        const regex = new RegExp(query, 'gi');
        const termsArray:string[] = [];
        [dictionary].forEach(obj => {
            for (let [term, def] of Object.entries(obj)) {
                termsArray.push(term);
            }
        })
        return termsArray.filter((val:string) => val.match(regex))
    }

    return (
        <>
            {findMatches(query).map((term, index, array) => {
                const regex = new RegExp(query, 'gi');
                const itemValue =  term.replace(regex, query);
                const max = array.length - 1;
                const min = 0;
                const maxCount = count <= max && index === count ?  max : count;
                const minCount = count >= 0 && index === count ?  min : count;
                console.log(array.length);
                return (
                    <li key={index}
                        className={`${(minCount || maxCount) ? 'bg-slate-400 text-slate-900':''} text-sm break-words text-slate-500 cursor-pointer hover:text-slate-200 hover:bg-slate-600 p-1`}
                        data-key="${term}">
                        <span>{itemValue}</span>
                    </li>
                )
            })}
        </>
    )

}
