import {useEffect} from "react";
import dictionary from "../dictionary.json";

interface Props{
    query: string;
    count: number;
    setCount: (count: number) => void
    setDef: (count: number) => void
}

export default function MatchesContainer({query, count, setCount, setDef}:Props){
    const findMatches = (query:string) => {
        const regex = new RegExp(query, 'gi');
        const termsArray:string[] = [];
        [dictionary].forEach(obj => {
            Object.keys(obj).forEach((term) => termsArray.push(term));
        })
        return termsArray.filter((val:string) => val.match(regex))
    }

    console.log(dictionary["e-series"])
    console.log();

    let tempArr = [];
    useEffect(() => {
        count >= tempArr.length -1 && setCount(tempArr.length -1)
        count <= 0 && setCount(0);
        // setDef()

    })

    return (
        <>
            {query && (<ul className="border-slate-600 border-2 rounded" >
                {findMatches(query).map((term, index, arr) => {
                    const regex = new RegExp(query, 'gi');
                    const itemValue =  term.replace(regex, query);
                    tempArr = arr;
                    return (
                        <li key={index}
                            className={`${count === index ? 'bg-slate-400 text-slate-900':''} text-sm break-words text-slate-500 cursor-pointer hover:text-slate-200 hover:bg-slate-600 p-1`}
                            data-key="${term}">
                            <span>{itemValue}</span>
                        </li>
                    )
                })}
            </ul>)}
        </>)

}
