import dictionary from "../dictionary.json";
import {useEffect} from "react";

interface Props{
    query: string;
    count: number;
    setCount:(index: number) => void
}

export default function MatchesContainer({query, count, setCount}:Props){
    const findMatches = (query:string) => {
        const regex = new RegExp(query, 'gi');
        const termsArray:string[] = [];
        [dictionary].forEach(obj => {
            for (let [term, def] of Object.entries(obj)) {
                termsArray.push(term);
            }
        })
        return termsArray.filter((val:string) => val.match(regex))
    }


    const resetCount = (arr: string[], index:number):string => {
        const maxCount = count <= arr.length -1 ? count : setCount(arr.length -1);
        const minCount = count >= 0 ? count : setCount(0);
        return maxCount === index || minCount === index ? 'bg-slate-400 text-slate-900':''
    }

    return (
        <>
            {query && (<ul className="border-slate-600 border-2 rounded" >
                {findMatches(query).map((term, index, array) => {
                    const regex = new RegExp(query, 'gi');
                    const itemValue =  term.replace(regex, query);



                    return (
                        <li key={index}
                            className={`${resetCount(array, index)} text-sm break-words text-slate-500 cursor-pointer hover:text-slate-200 hover:bg-slate-600 p-1`}
                            data-key="${term}">
                            <span>{itemValue}</span>
                        </li>
                    )
                })}
            </ul>)}
        </>)

}
